import { ContextualizedControl } from 'inspecjs';
export interface IFileInfo {
    filename: string;
    toolVersion: string;
    platform: string;
    duration: string;
}
export interface IStatistics {
    passed: number;
    failed: number;
    notApplicable: number;
    notReviewed: number;
    profileError: number;
    totalResults: number;
    passedTests: number;
    passingTestsFailedResult: number;
    failedTests: number;
    totalTests: number;
}
export interface ISeverity {
    none: number;
    low: number;
    medium: number;
    high: number;
    critical: number;
}
export interface ICompliance {
    level: string;
    color: string;
}
export interface IDetail {
    name: string;
    value: string;
    class?: string;
}
export interface IResultStatus {
    status: string;
    icon: string;
}
export interface IResultSeverity {
    severity: string;
    icon: string;
}
export interface IResultSet {
    filename: string;
    fileID: string;
    results: (ContextualizedControl & {
        details: IDetail[];
    } & {
        resultID: string;
    } & {
        resultStatus: IResultStatus;
    } & {
        resultSeverity: IResultSeverity;
    } & {
        controlTags: string[];
    })[];
}
export interface IIcons {
    [key: string]: string;
}
export interface IOutputData {
    tailwindStyles: string;
    tailwindElements: string;
    files: IFileInfo[];
    statistics: IStatistics;
    severity: ISeverity;
    compliance: ICompliance;
    resultSets: IResultSet[];
    showResultSets: boolean;
    showCode: boolean;
    exportType: string;
    icons: IIcons;
}
